con:configuration.scriptText:
import groovy.json.JsonSlurper;
import java.io.*;
import java.util.*;
def ResponseJson=messageExchange.response.contentAsString
def slurper= new JsonSlurper().parseText(ResponseJson)
String Token = context.expand(slurper.token_type)
String AccessToken = context.expand(slurper.access_token)
String Auth= "Bearer" +" "+ AccessToken
context.testCase.setPropertyValue("BearerAuth", Auth)
